import{o as t,c as n,d as a}from"./app.dd060d35.js";const e='{"title":"在画布上刷底色","description":"","frontmatter":{},"relativePath":"guide/know/drawColor.md","lastUpdated":1630486315988}',l={},o=[a('<h1 id="在画布上刷底色"><a class="header-anchor" href="#在画布上刷底色" aria-hidden="true">#</a> 在画布上刷底色</h1><p>    1. 在html中建立canvas 画布</p><p>    <code>&lt;canvas id=&quot;canvas&quot;&gt;&lt;/canvas&gt;</code></p><p>    2. 在js中获取canvas 画布</p><p>    <code>const canvas = document.getElementById(&#39;canvas&#39;)</code></p><p>    3. 使用canvas 获取webgl 绘图上下文</p><p>    <code>const gl = canvas.getContext(&#39;webgl&#39;)</code></p><p>    4. 指定将要用来清空绘图区的颜色</p><p>    <code>gl.clearColor(0,0,0,1)</code></p><p>    5. 使用之前指定的颜色，清空绘图区</p><p>    <code>gl.clear(gl_COLOR_BUFFER_BIT)</code></p><p>    完整代码</p><div class="language-"><pre><code>&lt;!DOCTYPE html&gt;\n&lt;html lang=&quot;en&quot;&gt;\n&lt;head&gt;\n  &lt;meta charset=&quot;UTF-8&quot;&gt;\n  &lt;meta http-equiv=&quot;X-UA-Compatible&quot; content=&quot;IE=edge&quot;&gt;\n  &lt;meta name=&quot;viewport&quot; content=&quot;width=device-width, initial-scale=1.0&quot;&gt;\n  &lt;title&gt;Document&lt;/title&gt;\n  &lt;style&gt;\n    * {\n      margin: 0;\n      padding: 0;\n    }\n    #canvas {\n      background-color: #000;\n    }\n  &lt;/style&gt;\n&lt;/head&gt;\n&lt;body&gt;\n  &lt;!-- 在html中建立canvas 画布 --&gt;\n  &lt;canvas id=&quot;canvas&quot;&gt;&lt;/canvas&gt;\n  &lt;script&gt;\n    // 在js中获取canvas 画布\n    const canvas = document.querySelector(&#39;#canvas&#39;)\n    // 设置canvas画布宽高\n    canvas.width = window.innerWidth\n    canvas.height = window.innerHeight\n    // 获取webgl绘图上下文\n    const gl = canvas.getContext(&#39;webgl&#39;)\n    // 指定将要用来清空绘图区的颜色\n    gl.clearColor(1,1,1,1)\n    // 使用之前指定的颜色，清空绘图区\n    gl.clear(gl.COLOR_BUFFER_BIT)\n  &lt;/script&gt;\n&lt;/body&gt;\n&lt;/html&gt;\n</code></pre></div>',13)];l.render=function(a,e,l,c,d,g){return t(),n("div",null,o)};export{e as __pageData,l as default};
