import{o as t,c as n,d as e}from"./app.9e536525.js";const o='{"title":"css颜色转webgl颜色","description":"","frontmatter":{},"headers":[{"level":3,"title":"css颜色转webgl颜色","slug":"css颜色转webgl颜色"}],"relativePath":"guide/know/two/changeColor.md","lastUpdated":1630548857803}',a={},l=[e('<h3 id="css颜色转webgl颜色"><a class="header-anchor" href="#css颜色转webgl颜色" aria-hidden="true">#</a> css颜色转webgl颜色</h3><hr><p>    1. 定义css颜色</p><p>    <code>const rgbaCss = &#39;(0,255,255,1)&#39;</code></p><p>     2. 定义正则      <code>\\(\\)表示先找到小括号 (.*)的()表示捕捉括号,(.*)捕捉括号里的所有东西</code></p><p>    <code>const reg = /\\((.*)\\)/</code></p><p>     3. 捕捉数据      <code>reg.exec(rgbaCss) 结果是个数组[&quot;(0,255,255,1)&quot;,&quot;0,255,255,1&quot;]</code></p><p>     <code>const rgbaStr = reg.exec(rgbaCss)[1]</code></p><p>     4. 加工数据      <code>webgl颜色r,g,b,a范围是0-1</code></p><p>     <code>const [r,g,b,a] = glColor.split(&#39;,&#39;).map((n,i) =&gt;i&lt;3?+n/255:+n)</code></p><p>     5. 声明颜色</p><p>     <code>gl.clearColor(r,g,b,a)</code></p><p>     6. 刷底色</p><p>     <code>gl.clear(gl.COLOR_BUFFER_BIT)</code></p><p>     完整代码</p><div class="language-"><pre><code>&lt;!DOCTYPE html&gt;\n&lt;html lang=&quot;en&quot;&gt;\n\n&lt;head&gt;\n  &lt;meta charset=&quot;UTF-8&quot;&gt;\n  &lt;meta http-equiv=&quot;X-UA-Compatible&quot; content=&quot;IE=edge&quot;&gt;\n  &lt;meta name=&quot;viewport&quot; content=&quot;width=device-width, initial-scale=1.0&quot;&gt;\n  &lt;title&gt;Document&lt;/title&gt;\n  &lt;style&gt;\n    * {\n      margin: 0;\n      padding: 0;\n    }\n\n    #canvas {\n      background-color: #000;\n    }\n  &lt;/style&gt;\n&lt;/head&gt;\n\n&lt;body&gt;\n  &lt;!-- 在html中建立canvas 画布 --&gt;\n  &lt;canvas id=&quot;canvas&quot;&gt;&lt;/canvas&gt;\n  &lt;script&gt;\n    // 在js中获取canvas 画布\n    const canvas = document.querySelector(&#39;#canvas&#39;)\n    // 设置canvas画布宽高\n    canvas.width = window.innerWidth\n    canvas.height = window.innerHeight\n    // 获取webgl绘图上下文\n    const gl = canvas.getContext(&#39;webgl&#39;)\n    // 指定将要用来清空绘图区的颜色\n    gl.clearColor(1, 1, 1, 1)\n    // 使用之前指定的颜色，清空绘图区\n    gl.clear(gl.COLOR_BUFFER_BIT)\n    const rgbaCss = &#39;(0,255,255,1)&#39;\n    const reg = /\\((.*)\\)/\n    const rgbaStr = reg.exec(rgbaCss)[1]\n    const [r, g, b, a] = rgbaStr.split(&#39;,&#39;).map((n, i) =&gt; i &lt; 3 ? +n / 255 : +n)\n\n    // 声明颜色\n    gl.clearColor(r, g, b, a)\n    // 刷底色\n    gl.clear(gl.COLOR_BUFFER_BIT)\n  &lt;/script&gt;\n&lt;/body&gt;\n\n&lt;/html&gt;\n</code></pre></div>',16)];a.render=function(e,o,a,c,g,s){return t(),n("div",null,l)};export{o as __pageData,a as default};
